import { Injectable } from '@angular/core';
import { Usuario, usuarios } from 'src/app/modelo/usuario.class';
import { Router } from '@angular/router';
import { locales, Local } from '../../../modelo/local.class';
import { HttpClient } from '@angular/common/http';




@Injectable({
  providedIn: 'root'
})
export class LoginService {

  constructor(private route:Router,private http:HttpClient) { }
  Usuario:string=''
  Contrasena:string=''
  usuarios:Usuario[]=[]


  async Validacion(usuario:string,contrasena:string):Promise<Boolean>{
    await localStorage.setItem("usuarioactivo","")
    await localStorage.setItem("localactivo","")
    await localStorage.setItem("adminactivo","")

    if(await localStorage.getItem("usuarioactivo")!=""){
      return true
    }
    await this.http.get<number>('http://127.0.0.1:8080/api/locallogin'+"/"+usuario+"/"+contrasena).subscribe((resp:number)=>{if (resp!=null){localStorage.setItem("localactivo",resp.toString());};});
    if (await this.validarusuario(usuario,contrasena)) {
      console.log("pri")
      return true

    } 
    if(await usuario=='admin' && contrasena=='123') {
      return true
    }

    else{
      return false
    }


}
  async validarusuario(usuario:string,contrasena:string){
    let usuarioact:number=0
    this.http.get<number>('http://127.0.0.1:8080/api/usuariologin'+"/"+usuario+"/"+contrasena).subscribe((resp:number)=>{if (resp!=null){usuarioact=resp ;localStorage.setItem("usuarioactivo",resp.toString());};});
    console.log("val")
    console.log(localStorage.getItem("usuarioactivo"))
    if(usuarioact=0){
      return true
    }else{
      return false
    }
  }





  async ingresar(){   
    if(await this.Validacion(this.Usuario,this.Contrasena)){
      this.route.navigate(['categorias'])
    }
    else{
      alert('Invalido el usuario y contraseña')
    }
  }
}
export const adminactivo:String[]=[]
export const usuarioactivo:Usuario[]=[]
export const localactivo:Local[]=[]
